import time
from typing import List


class Node:
    def __init__(self):
        self.children = {}
        self.end = None


class Solution:
    def findWords(self, board: List[List[str]], words: List[str]) -> List[str]:
        # list of words, and board of characters, words constructed from adj vert horiz cells only,
        # cannot use same character more than once

        # 2 options:
        # 1.    build a trie of words in wordList and scan board to find start position of words in trie
        #       traverse trie until adjacent cells do not contain characters in trie or word found or all
        #       elements explored
        # 2.    no trie, traverse board for each char get set of words from wordList containing that char
        #       as first character, continue visiting adjacent cells until no words remain in set, if all
        #       characters of a word in the set have been seen in this explore() add the word to results
        #   option 1 would be better if the wordList and/or board are large, option 2 the inverse
        def inBound(y, x):
            return 0 <= y < n and 0 <= x < m

        def build_trie():
            trie = Node()
            for word in words:
                node = trie
                for c in word:
                    if c not in node.children:
                        node.children[c] = Node()
                    node = node.children[c]
                node.end = word

            return trie

        def explore(i, j, visited, node):
            if node.end:
                res.add(node.end)
            for ii, jj in [(0, -1), (0, 1), (1, 0), (-1, 0)]:
                y, x = i + ii, j + jj
                if not inBound(y, x) or (y, x) in visited or board[y][x] not in node.children:
                    continue

                explore(y, x, visited.union({(y, x)}), node.children[board[y][x]])

        trie, res, n, m = build_trie(), set(), len(board), len(board[0])
        for i in range(n):
            for j in range(m):
                if board[i][j] in trie.children:
                    explore(i, j, {(i, j)}, trie.children[board[i][j]])

        return list(res)

    def findWordsOptimized(self, board: List[List[str]], words: List[str]) -> List[str]:
        def in_bounds(y, x):
            return 0 <= y < n and 0 <= x < m

        def build_trie():
            trie = Node()
            for word in words:
                node = trie
                for c in word:
                    if c not in node.children:
                        node.children[c] = Node()
                    node = node.children[c]
                node.end = word

            return trie

        def explore(i, j, visited, parent):
            node = parent.children[board[i][j]]
            if node.end:
                res.append(node.end)
                node.end = None

            for ii, jj in [(0, -1), (0, 1), (1, 0), (-1, 0)]:
                y, x = i + ii, j + jj
                if not in_bounds(y, x) or (y, x) in visited or board[y][x] not in node.children:
                    continue

                explore(y, x, visited.union({(y, x)}), node)

            if not node.children:
                parent.children.pop(board[i][j])
                del node

        trie, res, n, m = build_trie(), [], len(board), len(board[0])
        for i in range(n):
            for j in range(m):
                if board[i][j] in trie.children:
                    explore(i, j, {(i, j)}, trie)

        return res


